import React, { useState, useEffect } from 'react';
import MainMenu from './MainMenu';
import Background from './Background';
import Bush from './Bush';
import Timer from './Timer';
import Cloud from './Cloud';
import GameOver from './GameOver';

const Game = () => {
  const [score, setScore] = useState(0);
  const [gameStarted, setGameStarted] = useState(false);
  const [clouds, setClouds] = useState([]); // Store cloud configurations
  const [time, setTime] = useState(0);
  const [gameOver, setGameOver] = useState(false); // Add gameOver state

  useEffect(() => {
    // Generate random initialX positions for clouds when the game starts
    const newClouds = Array.from({ length: 9 }, (_, index) => ({
      initialX: Math.random() * (screen.availWidth-200) + 200,
      y: 30 + index * 20, // Adjust Y positions as needed
      speed: 1 + Math.random() * 2, // Adjust speed as needed
    }));
    setClouds(newClouds);
  }, []);

  const handleStartGame = () => {
    setGameStarted(true);
  };

  return (
    <div className="game-container">
      {gameStarted ? (
        <>
          <Background score={score} time={time} />

          <Bush setScore={setScore} score={score} setTime={setTime} time={time}/>
          <Timer setTime={setTime} time={time} />
          {/* Render clouds */}
          {clouds.map((cloud, index) => (
            <Cloud key={index} {...cloud} />
          ))}
        </>
      ) : (
        <MainMenu
          onStartGame={handleStartGame}
          onSettings={() => {} /* Implement settings */}
          onHelp={() => {} /* Implement help */}
        />
      ) } : gameOver ? (
            <GameOver score={score} time={time} /> // Render GameOver component when gameOver is true
      )
    </div>
  );
};

export default Game;
