import React, { useState, useEffect } from 'react';

const Bush = ({ setScore }) => {
  const [bushes, setBushes] = useState([]);
  const [moleIndex, setMoleIndex] = useState(null);
  const [moleVisible, setMoleVisible] = useState(false);

  useEffect(() => {
    const newBushes = Array.from({ length: 7 }, (_, index) => ({
      x: Math.random() * 800,
      y: Math.random() * 400,
    }));
    setBushes(newBushes);

    const moleInterval = setInterval(() => {
      const newIndex = Math.floor(Math.random() * 7);
      setMoleIndex(newIndex);
      setMoleVisible(true);

      setTimeout(() => {
        setMoleVisible(false);
      }, 2000);
    }, 3000); // Set interval to 3 seconds

    return () => {
      clearInterval(moleInterval);
    };
  }, []);

  const handleMoleClick = () => {
    if (moleVisible && moleIndex !== null) {
      setScore(prevScore => prevScore + 1);
      setMoleVisible(false);
    }
  };

  return (
    <div className="bush-container">
      {bushes.map((bush, index) => (
        <div
          key={index}
          className="bush"
          style={{
            position: 'absolute',
            left: `${bush.x}px`,
            top: `${bush.y}px`,
            width: '50px',
            height: '50px',
            borderRadius: '50%',
            backgroundColor: 'green',
            zIndex: 0,
          }}
          onClick={handleMoleClick}
        >
          {moleVisible && moleIndex === index && (
            <div
              className="mole"
              style={{
                backgroundColor: 'brown',
                width: '30px',
                height: '30px',
                borderRadius: '50%',
                position: 'absolute',
                top: '-15px',
                left: '10px',
                zIndex: 0,
              }}
            ></div>
          )}
        </div>
      ))}
    </div>
  );
};

export default Bush;
