// Game.js
import React, { useState } from 'react';
import MainMenu from './MainMenu';
import Background from './Background';
import RunTime from './RunTime'; // Import the RunTime component
import Cloud from './Cloud';
import Timer from './Timer';
import GameOver from './GameOver';
import Settings from './Settings'; // Import the Settings component

const Game = () => {
  const [score, setScore] = useState(0);
  const [gameStarted, setGameStarted] = useState(false);
  const [clouds, setClouds] = useState([]); // Store cloud configurations
  const [time, setTime] = useState(0); // Set an initial time
  const [gameOver, setGameOver] = useState(false); // Add gameOver state
  const [penalty, setPenalty] = useState(0);
  const initialBushes = 5;
  const [difficultyMultiplier, setDifficultyMultiplier] = useState(1);
  const [showSettings, setShowSettings] = useState(false);

  const handleStartGame = () => {
    setGameStarted(true);
  };

  const handleGameOver = () => {
    setGameStarted(false);
    setGameOver(true);
  };

  const handleSettings = () => {
    setShowSettings(true);
  };

  const handleCloseSettings = () => {
    setShowSettings(false);
  };

  return (
    <div className="game-container">
      {gameStarted ? (
        <>
          <Background score={score} time={time} penalty={penalty}/>
          <Timer setTime={setTime}/>
          {/* Render the RunTime component */}
          <RunTime setScore={setScore} score={score} onGameOver={handleGameOver} setPenalty={setPenalty} initialBushes={initialBushes} difficultyMultiplier={difficultyMultiplier}/>
          {/* Render clouds */}
          {clouds.map((cloud, index) => (
            <Cloud key={index} {...cloud} />
          ))}
        </>
      ) : (
        <>
          {gameOver ? (
            <GameOver score={score} time={time} penalty={penalty} initialBushes={initialBushes} difficultyMultiplier={difficultyMultiplier}/>
          ) : (
            <MainMenu
              onStartGame={handleStartGame}
              onSettings={handleSettings} // Open settings when clicked
              onHelp={() => {} /* Implement help */}
            />
          )}
        </>
      )}
      {/* Render the Settings component when showSettings is true */}
      {showSettings && (
        <Settings
          difficultyMultiplier={difficultyMultiplier}
          setDifficultyMultiplier={setDifficultyMultiplier}
          initialBushes={initialBushes}
          setInitialBushes={setInitialBushes}
          onClose={handleCloseSettings}
        />
      )}
    </div>
  );
};

export default Game;
