// Settings.js
import React, { useState } from 'react';

const Settings = ({ difficultyMultiplier, setDifficultyMultiplier, initialBushes, setInitialBushes, onClose }) => {
  const [sliderValue, setSliderValue] = useState(difficultyMultiplier);
  const [bushesSliderValue, setBushesSliderValue] = useState(initialBushes);

  const handleSliderChange = (event) => {
    const value = parseFloat(event.target.value);
    setSliderValue(value);
  };

  const handleBushesSliderChange = (event) => {
    const value = parseInt(event.target.value);
    setBushesSliderValue(value);
  };

  const handleSaveSettings = () => {
    setDifficultyMultiplier(sliderValue);
    setInitialBushes(bushesSliderValue);
    onClose();
  };

  return (
    <div className="settings-container">
      <h2>Settings</h2>
      <div className="slider-container">
        <label htmlFor="difficultySlider">Difficulty:</label>
        <input
          type="range"
          id="difficultySlider"
          min={1.0}
          max={5.0}
          step={0.1}
          value={sliderValue}
          onChange={handleSliderChange}
        />
        <span>{`Difficulty: ${sliderValue}`}</span>
      </div>
      <div className="slider-container">
        <label htmlFor="bushesSlider">Initial Bushes:</label>
        <input
          type="range"
          id="bushesSlider"
          min={5}
          max={40}
          step={1}
          value={bushesSliderValue}
          onChange={handleBushesSliderChange}
        />
        <span>{`Initial Bushes: ${bushesSliderValue}`}</span>
      </div>
      <button onClick={handleSaveSettings}>Save</button>
    </div>
  );
};

export default Settings;
