// RunTime.js
import React, { useState, useEffect } from 'react';
import Bush from './Bush';

const RunTime = ({ setScore, score, onGameOver, setPenalty }) => {
  const bushSize = 80;
  const initialBushesSet = 7;

  const [bushes, setBushes] = useState([]);
  const [moleIndex, setMoleIndex] = useState(null);
  const [nextMoleIndex, setNextMoleIndex] = useState(null);
  const [moleVisible, setMoleVisible] = useState(false);
  const [molePosition, setMolePosition] = useState({ x: 0, y: 0 });
  const [destroyingBush, setDestroyingBush] = useState(null);

  const generateRandomPosition = () => {
    const x = Math.random() * (screen.availWidth - 120);
    const y = (screen.availHeight / 2) + (Math.random() * ((screen.availHeight / 2) - 120));
    return { x, y };
  };

  const generateNonOverlappingPosition = (newBushes) => {
    let newPosition;
    let isOverlapping;

    do {
      isOverlapping = false;
      newPosition = generateRandomPosition();

      for (const bush of newBushes) {
        if (
          newPosition.x < bush.x + bushSize &&
          newPosition.x + bushSize > bush.x &&
          newPosition.y < bush.y + bushSize &&
          newPosition.y + bushSize > bush.y
        ) {
          isOverlapping = true;
          break;
        }
      }
    } while (isOverlapping);

    return newPosition;
  };

  useEffect(() => {
    const initialBushes = [];
    while (initialBushes.length < initialBushesSet) {
      const newPosition = generateNonOverlappingPosition(initialBushes);
      initialBushes.push(newPosition);
    }

    setBushes(initialBushes);
  }, []);

  useEffect(() => {
    const moleInterval = setInterval(() => {
      setMoleVisible(true);

      setTimeout(() => {
        setMoleVisible(false);
      }, 1250 - score * 25);
    }, 2000 - score * 35);

    return () => {
      clearInterval(moleInterval);
    };
  }, [score]);

  useEffect(() => {
    setNextMoleIndex(Math.floor(Math.random() * bushes.length));
    if (!moleVisible && nextMoleIndex !== null) {
      setMoleIndex(nextMoleIndex);
      setMolePosition({ ...bushes[nextMoleIndex] });
    }
  }, [moleVisible, nextMoleIndex, bushes]);

  const handleMoleClick = (index) => {
    if (moleVisible && index === moleIndex) {
      setScore((prevScore) => prevScore + 1);
      createNewBushes(index,true);
      setMoleVisible(false);
    } else if (index !== moleIndex) {
      setPenalty((prevPenalty) => prevPenalty + 1);
      createNewBushes(index,false);
    }
  };

  const handleBushClick = (index) => {
    if(moleVisible && index === moleIndex){
        createNewBushes(index, true);
      setScore((prevScore) => prevScore + 1);
      setMoleVisible(false);
    }else{
      setPenalty((prevPenalty) => prevPenalty + 1);
      createNewBushes(index, false);
    }
  };

  const createNewBushes = (indexToRemove, mole) => {
    const newBushes = bushes.filter((_, index) => index !== indexToRemove);

    if (Math.random() < 0.75) {
      // 75% chance to execute this block of code
      const newPosition = generateNonOverlappingPosition(newBushes);
      newBushes.push(newPosition);

      // Animate the circle components one after the other
      var circles = document.querySelectorAll(`.bush${indexToRemove} .circle`);
      if(mole===true){
        const circles = document.querySelectorAll('.bush.with-mole .circle');
      }else{
        const circles = document.querySelectorAll(`.bush${indexToRemove} .circle`);
      }
      circles.forEach((circle, index) => {
        setTimeout(() => {
          circle.style.transform = 'scale(0)'; // Animate the circle to disappear
        }, index * 100); // Adjust the delay as needed (e.g., index * 100ms)
      });

      // After the animation duration, update the bushes with new positions
      setTimeout(() => {
        setBushes(newBushes);
        // Reset the circle components to their original state
        circles.forEach((circle) => {
          circle.style.transform = 'scale(1)';
        });
      }, circles.length * 100); // Adjust the duration based on the number of circles
    }

    if (newBushes.length <= 0) {
      console.log("gameoverlmao");
      onGameOver();
    }
  };


  return (
    <>
      {/* Render the Bush component */}
      <Bush
        bushes={bushes}
        moleIndex={moleIndex}
        moleVisible={moleVisible}
        molePosition={molePosition}
        handleMoleClick={handleMoleClick}
        handleBushClick={handleBushClick}
      />
    </>
  );
};

export default RunTime;
