import React, { useState, useEffect } from 'react';

const Bush = ({ setScore }) => {
  const [bushes, setBushes] = useState([]);
  const [moleIndex, setMoleIndex] = useState(null);
  const [moleVisible, setMoleVisible] = useState(false);

  useEffect(() => {
    const newBushes = Array.from({ length: 7 }, (_, index) => ({
      x: Math.random() * (screen.availWidth - 300) + 150,
      y: Math.random() * (screen.availHeight/2 - 100) + screen.availHeight/2,
    }));
    setBushes(newBushes);

    const moleInterval = setInterval(() => {
      const newIndex = Math.floor(Math.random() * 7);
      setMoleIndex(newIndex);
      setMoleVisible(true);

      setTimeout(() => {
        setMoleVisible(false);
      }, 1250);
    }, 2000);

    return () => {
      clearInterval(moleInterval);
    };
  }, []);

  const handleMoleClick = (index) => {
    createNewBushes(index);
    if (moleVisible && index === moleIndex) {
      setScore(prevScore => prevScore + 1);
      setMoleVisible(false);
    }
  };

  const handleBushClick = (index) => {
    if (!moleVisible && index === moleIndex) {
      // If the clicked bush has no mole, do nothing
      return;
    }
    createNewBushes(index); // Always create a new bush when a bush is clicked
  };

  const createNewBushes = (indexToRemove) => {
    // Remove the clicked bush and create a new one
    const newBushes = bushes.filter((_, index) => index !== indexToRemove);
    newBushes.push({
      x: Math.random() * (screen.availWidth - 300) + 150,
      y: Math.random() * (screen.availHeight/2 - 100) + screen.availHeight/2,
    });
    setBushes(newBushes);
  };

  return (
    <div className="bush-container">
      {bushes.map((bush, index) => (
        <div
          key={index}
          className={`bush ${moleIndex === index ? 'with-mole' : ''}`}
          style={{
            position: 'absolute',
            left: `${bush.x}px`,
            top: `${bush.y}px`,
            width: '50px',
            height: '50px',
            borderRadius: '50%',
            backgroundColor: 'darkgreen',
            zIndex: 1,
          }}
          onClick={() => moleVisible ? handleMoleClick(index) : handleBushClick(index)}
        >
          {moleVisible && moleIndex === index && (
            <div
              className="mole"
              style={{
                backgroundColor: 'brown',
                width: '30px',
                height: '30px',
                borderRadius: '50%',
                position: 'absolute',
                top: '-15px',
                left: '10px',
              }}
            ></div>
          )}
        </div>
      ))}
    </div>
  );
};

export default Bush;
