import React, { useState } from 'react';
import MainMenu from './MainMenu';
import Background from './Background';
import Bush from './Bush';
import Timer from './Timer';
import Cloud from './Cloud'; // Import the Cloud component

const Game = () => {
  const [score, setScore] = useState(0);
  const [gameStarted, setGameStarted] = useState(false);

  const handleStartGame = () => {
    setGameStarted(true);
  };

  return (
    <div className="game-container">
      {gameStarted ? (
        <>
          <Background />
          <Bush setScore={setScore} />
          <Timer setScore={setScore} />
          <p className="score">Score: {score}</p>
          {/* Render clouds */}
          <Cloud initialX={screen.availWidth} y={100} speed={2} />
          <Cloud initialX={screen.availWidth} y={200} speed={1.5} />
          <Cloud initialX={screen.availWidth} y={300} speed={1} />
        </>
      ) : (
        <MainMenu
          onStartGame={handleStartGame}
          onSettings={() => {} /* Implement settings */}
          onHelp={() => {} /* Implement help */}
        />
      )}
    </div>
  );
};

export default Game;
