// Bush.js
import React from 'react';

const Bush = ({ bushes, moleIndex, moleVisible, molePosition, handleMoleClick, handleBushClick }) => {
    const circleSize = 50;
    const bottomCircleY = 40;
    const topCircleY = 25;
    const bottomCircleSpacing = 20;
    const topCircleSpacing = 25;

    const renderCircles = () => {
        const circles = [];

        const addCircleWithDelay = (left, top) => {
          circles.push(
            <div
              key={circles.length}
              className="circle"
              style={{
                width: `${circleSize}px`,
                height: `${circleSize}px`,
                borderRadius: '50%',
                backgroundColor: 'darkgreen',
                position: 'absolute',
                left: `${left}px`,
                top: `${top}px`,
              }}
            ></div>
          );

          if (circles.length < 5) {
            setTimeout(() => {
              const nextLeft = circles.length * bottomCircleSpacing;
              addCircleWithDelay(nextLeft, bottomCircleY);
            }, 40);
          }
        };

        // Start adding circles
        addCircleWithDelay(0, bottomCircleY);

        return (
          <div className="circle-cluster">
            {circles}
          </div>
        );
      };


  return (
    <div className="bush-container">
      {bushes.map((bush, index) => (
        <div
          key={index}
          className={`bush ${moleIndex === index ? 'with-mole' : ''}`}
          style={{
            position: 'absolute',
            left: `${bush.x}px`,
            top: `${bush.y}px`,
            zIndex: 2, // Set a higher zIndex for the bush class
          }}          
          onClick={() => (moleVisible ? handleMoleClick(index) : handleBushClick(index))}
        >
          {renderCircles()}
          {moleVisible && moleIndex === index && (
            <div
              className="mole"
              style={{
                backgroundColor: 'brown',
                width: '30px',
                height: '30px',
                borderRadius: '50%',
                position: 'absolute',
                top: `${molePosition.y - bush.y + 10}px`,
                left: `${molePosition.x - bush.x + 30}px`,
                zIndex: 1, // Set a lower zIndex for the mole class
              }}
            ></div>
          )}
        </div>
      ))}
    </div>
  );  
};

export default Bush;
